// page init
jQuery(function() {
	initTabs();
	initCustomForms();
	initTouchNav();
	initUiSlider();
	initCustomOpenClose();
	initTooltipFix();
	initSteps();
	initSetHeight();
});

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		wrapNative: false,
		wrapNativeOnMobile: false
	});
	jcf.replaceAll();
}

// content tabs init
function initTabs() {
	jQuery('ul.tabset').tabset({
		tabLinks: 'a',
		defaultTab: false
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('.header').each(function() {
		new TouchNav({
			navBlock: this,
			menuOpener: '.opener',
			menuItems: '.dropdown',
			menuDrop: '.drop'
		});
	});
}

// tooltip fix init
function initTooltipFix() {
	var body = jQuery('body');
	var rightSideClass = 'right-side';

	jQuery('.tooltip').each(function() {
		var item = jQuery(this);
		var tooltip = item.find('.t-slide');

		if (tooltip.length) {
			item.on('click mouseenter', function(e) {
				e.preventDefault();
				item.removeClass(rightSideClass);

				if (item.offset().left + item.outerWidth(true) + tooltip.outerWidth(true) + 10 > body.width()) {
					item.addClass(rightSideClass);
				}
			});
		}
	});
}

// set height init
function initSetHeight() {
	var win = jQuery(window);
	var block = jQuery('#content .info-module');
	var tabset = jQuery('#content .tabset');
	var header = jQuery('.header');

	jQuery('.tab-module .tab').each(function() {
		var tab = jQuery(this);
		var winHeight;

		function resizeHandler() {
			winHeight = getWindowHeight();

			if (tab.is(':visible')) {
				tab.css({
					minHeight: winHeight - block.outerHeight(true) - tabset.outerHeight(true) - header.outerHeight(true) - 15
				});
			}
		}

		win.on('resize orientationchange refreshTabs', resizeHandler);
	});

	function getWindowHeight() {
		return typeof window.innerHeight === 'number' ? window.innerHeight : document.documentElement.clientHeight;
	}
}

// ui slider init
function initUiSlider() {
	jQuery('.ui-slider').each(function() {
		var holder = jQuery(this);
		var slider = holder.find('.slider');
		var valueField = holder.find('.value');
		var sliderMin = parseInt(slider.data('min'), 10) || 0;
		var sliderMax = parseInt(slider.data('max'), 10) || 100;
		var sliderValue = parseInt(slider.data('value'), 10) || 0;
		var sliderStep = parseInt(slider.data('step'), 10) || 1;
		var values = slider.data('values');
		var sliderText = slider.data('text');
		var dataField = slider.data('field');
		var resultField = jQuery(slider.data('result'));
		var valuesArr = [];

		slider.slider({
			range: 'min',
			value: sliderValue,
			min: sliderMin,
			max: sliderMax,
			step: sliderStep,
			disabled: slider.data('disabled') || false,
			slide: function(event, ui) {
				sliderValue = ui.value;
				refreshText();
			},
			change: function(event, ui) {
				sliderValue = ui.value;
				refreshText();
				holder.trigger('validateForm');
			}
		});

		function refreshText(flag) {
			if (flag) {
				sliderValue = 0;
			}
			slider.attr('data-curr-value', sliderValue);
			if (slider.hasClass('money-slider')) {
				var value = (sliderText ? sliderText : '') + formatToMoney(sliderValue, ',');
				valueField.val(value);

				if (resultField.length) {
					resultField.text(value);
				}
			} else {
				valueField.val(sliderValue + (sliderText ? (' ' + sliderText) : ''));
			}

			if (values) {
				valuesArr = values.split(', ');

				if (dataField) {
					var index = valuesArr[sliderValue - 1].indexOf('-');
					var text = '';

					if (index !== -1) {
						text = valuesArr[sliderValue - 1].slice(index);
					}

					jQuery(dataField).html('<strong>' + sliderValue + '</strong>' + ' ' + text);
				}
			}
		}

		refreshText(slider.attr('data-change') ? false : true);
	});

	function formatToMoney(number, sep) {
		return String(number).replace(/\B(?=(\d{3})+(?!\d))/g, sep);
	}
}

// custom open close init
function initCustomOpenClose() {
	jQuery('.js-open-close').each(function() {
		var holder = jQuery(this);
		var opener = holder.find('.form-item :checkbox');
		var slide = holder.find('.radio-group').hide();
		var checkboxes = slide.find(':checkbox');

		opener.on('change', function() {
			var checkbox = jQuery(this);

			if (checkbox.prop('checked') === false) {
				slide.slideDown();
			} else {
				slide.slideUp();
			}
		});

		checkboxes.on('change', function(e) {
			var activeNum = 0;

			checkboxes.each(function() {
				if (jQuery(this).prop('checked') === true) {
					activeNum++;
				}
			});

			if (activeNum > 3) {
				jQuery(this).prop('checked', false);
			}
		});
	});
}

// steps init
function initSteps() {
	var win = jQuery(window);
	var tabModule = jQuery('.js-tab-module');
	var noteBox = jQuery('.js-note');

	jQuery('.steps-accordion').steps({
		opener: 'a.opener, a.edit',
		slider: '>.slide',
		filledClass: 'filled',
		animSpeed: 300,
		onInit: function(self) {
			self.stepsBehaviour = {
				step0: {
					init: function(block) {}
				},
				step1: {
					init: function(block) {}
				},
				step2: {
					init: function(block) {}
				},
				step3: {
					init: function(block) {
						block.addClass(self.options.completeClass);
					}
				}
			};
			self.stepsBehaviour.step0.init(self.sliders.eq(0));

			jQuery('.check-holder').each(function() {
				var holder = jQuery(this);
				var checkbox = holder.find('.value-checkbox');
				var slider = holder.find('.slider');
				var resultField = jQuery(slider.data('result'));
				var parent = resultField.closest('.field-value');
				var row = holder.find('.validate-field');
				var item = holder.closest('.slide');

				if (parent.length) parent.hide();

				checkbox.on('change', function() {
					if (checkbox.is(':checked')) {
						slider.slider({
							disabled: false
						});

						row.addClass('validate-row');

						if (parent.length) parent.show();
					} else {
						slider.slider({
							value: 0,
							disabled: true
						}).trigger('slidechange');

						if (parent.length) parent.hide();
						row.removeClass('validate-row');
					}

					self.validateForm(item);
				});
			});
		},
		onShow: function(self, slide) {
			var item = slide.closest('.item');
			self.items.removeClass(self.options.filledClass);
			item.prevAll().addClass(self.options.filledClass);

			if (self.items.index(item) >= 3) {
				tabModule.removeClass(self.options.disabledClass);
			}

			if (self.items.index(item) >= 2) {
				noteBox.hide();
			} else {
				noteBox.show();
			}

			self.items.each(function() {
				var currItem = jQuery(this);
				var dataContent = currItem.data('content');

				if (dataContent && currItem.hasClass(self.options.filledClass)) {
					jQuery('[data-id=' + dataContent + ']').show();
				} else {
					if (!currItem.hasClass(self.options.completeClass)) {
						jQuery('[data-id=' + dataContent + ']').hide();
					}
				}
			});

			if (self.stepsBehaviour['step' + self.currentStep]) {
				self.stepsBehaviour['step' + self.currentStep].init(slide.closest('.item'));
			}

			setTimeout(function() {
				win.trigger('refreshTabs');
			}, 10);
		},
		onSuccess: function(self) {
			if (self.currentStep === 2) {
				var block = self.items.eq(self.currentStep);

				if (block.hasClass(self.options.nexActiveClass)) {
					var section = jQuery('[data-id=' + block.find('form').data('content') + ']');
					var result = jQuery(block.find('form').data('result'));

					section.show();
					result.show();
					win.trigger('refreshTabs');
				}
			}
		}
	});
}

